import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useToast } from '../Context/ToastContext';
import { Link, useNavigate } from 'react-router-dom';

function Signup() {



    const Navigate = useNavigate  ();
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const showToast = useToast();
  const API_URL = process.env.REACT_APP_BACKEND_URL;

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
        Navigate('/')
    }
},[Navigate])

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_URL}/signup`, {
        name,
        email,
        password,
      });
      if(response){
        showToast('Signup Successful', 'success');
        setName('')
        setEmail('')
        setPassword('')

      }
    } catch (error) {
      if (error.response && error.response.status === 409) {
        showToast('Email already exists', 'error');
      } else {
        showToast('Signup failed', 'error');
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center">
  <div className="flex w-full max-w-4xl mx-auto">
    {/* Left side - Image/Content */}
    <div className="w-1/2 flex items-center justify-center bg-gray-200">
      <img src="your-image-url.jpg" alt="A descriptive alternative text" className="max-w-full h-auto rounded-lg" />
      {/* You can replace the img tag with any other content you wish to display on the left side */}
    </div>

    {/* Right side - Signup Form */}
    <div className="w-1/2 p-6 bg-white border border-gray-300 rounded-lg shadow-lg">
      <h2 className="text-3xl font-semibold text-center mb-6">Signup</h2>
      <form onSubmit={handleSignup}>
        {/* Form fields remain unchanged */}
        <div className="mb-4">
          {/* Name, Email, Password Inputs, and Signup Button */}
        </div>
        <button
          type="submit"
          className="w-full py-2 px-4 bg-blue-500 text-white rounded hover:bg-blue-700"
        >
          Signup
        </button>
        <div className='text-center py-3'>Already a Member? <Link to={'/login'} className='text-gray-600'>Log In</Link></div>
      </form>
    </div>
  </div>
  <ToastContainer
    position="top-right"
    autoClose={1000}
    limit={1}
    hideProgressBar={false}
    closeOnClick
    pauseOnHover
  />
</div>

  );
}

export default Signup;
