import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../Context/AuthContext';


const Navbar = () => {
    const Navigate = useNavigate();
    const { isLoggedIn, setIsLoggedIn,name } = useAuth();


    const handleLogout = (e) => {
        e.preventDefault();
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        Navigate('/login');
    };



    return (
        <nav className="bg-slate-100 p-4">
        <div className="container mx-auto flex justify-between items-center">
            {/* Left side - Home, History, Contact */}
            {isLoggedIn ? (
                <div className="hidden md:flex space-x-4">
                    <Link to="/" className="text-white hover:bg-gray-700 hover:text-white rounded-md px-3 py-2 text-sm font-medium align-center">
                        Home
                    </Link>
                    <Link to="/history" className="text-white hover:bg-gray-700 hover:text-white rounded-md px-3 py-2 text-sm font-medium align-center">
                        History
                    </Link>
                    <Link to="/contact" className="text-white hover:bg-gray-700 hover:text-white rounded-md px-3 py-2 text-sm font-medium align-center">
                        Contact
                    </Link>
                </div>
            ) : (
                <div> {/* This empty div ensures the layout structure remains consistent regardless of login state */} </div>
            )}

            {/* Right side - Login and Signup */}
            {!isLoggedIn && (
                <div className="flex space-x-6">
                    <div className="text-blue-600">
                        <Link to="/login">Login</Link>
                    </div>
                    <div className="text-blue-600">
                        <Link to="/signup">Signup</Link>
                    </div>
                </div>
            )}

            {/* If logged in, you might want to show user info or logout button here */}
            {isLoggedIn && (
                <div className="flex space-x-4">
                    {/* Example: Logout button, adjust as needed */}
                    <button className="text-red-600 text-sm" onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            )}
        </div>
    </nav>


    );
};

export default Navbar;
